        private readonly string _address;

        public EmailAddress(string address)
        {
            if (string.IsNullOrEmpty(address))
            {
                throw new ArgumentException("Value cannot be null or empty.", nameof(address));
            }

            address = address.ToLower();
            var m = _validationRegex.Match(address);
            if (!m.Success)
            {
                throw new FormatException($"Provided string '{address}' is not a valid email address");
            }

            _address = address;
        }

        public bool Equals(EmailAddress other)
        {
            if (ReferenceEquals(null, other))
            {
                return false;
            }

            if (ReferenceEquals(this, other))
            {
                return true;
            }

            return string.Equals(_address, other._address);
        }


        public override string ToString()
        {
            return _address;
        }

        public override bool Equals(object obj)
        {
            if (ReferenceEquals(null, obj))
            {
                return false;
            }

            if (ReferenceEquals(this, obj))
            {
                return true;
            }

            if (obj.GetType() != GetType())
            {
                return false;
            }

            return Equals((EmailAddress) obj);
        }

        public override int GetHashCode()
        {
            return _address != null ? _address.GetHashCode() : 0;
        }

        public static bool operator == (EmailAddress left, EmailAddress right)
        {
            return Equals(left, right);
        }

        public static bool operator != (EmailAddress left, EmailAddress right)
        {
            return !Equals(left, right);
        }

        // Explicitní - na vyžádání, musím použít operátor závorky () cast
        public static explicit operator EmailAddress(string input)
        {
            return new EmailAddress(input);
        }

        // Implicitní - udělá se samo bez operátoru závorky ()
        public static implicit operator string(EmailAddress input)
        {
            return input.ToString();
        }

        public static bool IsValidEmail(string input)
        {
            try
            {
                var e = new EmailAddress(input);
                return true;
            }
            catch (FormatException e) // Nevhodná kontrola pomocí výjimky
            {
                return false;
            }
        }
